set st=$$$OK
Try
{
	set SchoolQuery="select ID, Actual, Cost, DateActualEnd, DateActualStart, TypeLesson->name As name, TypeLesson->ID as TypeLessonID, school "_
	"from SPD.Price where school->id="_ID
	set st=##class(SPD.School).GetJSONData(SchoolQuery)
}
catch ex{
set st=ex.AsStatus()
